import React, { useState, useEffect } from "react";

function Total() {
  var unirest = require("unirest");

  var req = unirest("GET", "https://covid-19-data.p.rapidapi.com/totals");

  req.query({
    format: "json",
  });

  req.headers({
    "x-rapidapi-host": "covid-19-data.p.rapidapi.com",
    "x-rapidapi-key": "1d4a63eb5dmshedfd7c1ab6075cep1a20bfjsnee48c2d9b899",
  });
  const [info, setInfo] = useState(null);
  const [confirmed, setConfirmed] = useState(0);
  const [recovered, setRecovered] = useState(0);
  const [deaths, setDeaths] = useState(0);
  req.end(function (res) {
    if (res.error) {
      console.log(res.error);
      throw new Error(res.error);
    }
    setInfo(res.body);
    setConfirmed(info.confirmed);
    setRecovered(info.recovered);
    setDeaths(info.deaths);
    console.log(res.body);
  });
  return (
    <div>
      <h1>Confirmed: {confirmed}</h1>
      <h1>recovered: {recovered}</h1>
      <h1>Deaths: {deaths}</h1>
    </div>
  );
}
export default Total;
