import React, { useState } from "react";
import { Marker, InfoWindow } from "react-google-maps";
import Info from "./Info.js";

class Markers extends React.Component {
  constructor() {
    super();
    this.state = {
      cases: [],
      info: [],
      displayInfo: false,
    };
  }

  componentDidMount() {
    this.data();
  }

  data() {
    fetch(
      "https://opendata.arcgis.com/datasets/bbb2e4f589ba40d692fab712ae37b9ac_1.geojson"
    )
      .then((response) => response.json())
      .then(
        (responseJson) => this.setState({ cases: responseJson.features })
        // jsonData is parsed json object received from url
      );
    console.log(this.state.cases);
  }

  displayInfor(lt, ln) {
    this.setState({
      info: [{ lat: lt }, { lng: ln }],
      displayInfo: !this.state.displayInfo,
    });
    console.log(this.state.info);
  }
  render() {
    let info = null;
    if (this.state.displayInfo) {
      info = <Info lat={this.state.info.lat} lng={this.state.info.lng} />;
    }
    return this.state.cases.map(function (cases) {
      return (
        <Marker
          key={cases.properties.OBJECTID}
          position={{
            lat: cases.properties.Lat,
            lng: cases.properties.Long_,
          }}
          onClick={() =>
            this.setState({
              info: [
                { lat: cases.properties.Lat },
                { lng: cases.properties.Long_ },
              ],
              displayInfo: !this.state.displayInfo,
            })
          }
        >
          {info}
        </Marker>
      );
    });
  }
}

export default Markers;
