import React, { useState } from "react";
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  InfoWindow,
} from "react-google-maps";
import Markers from "./components/Markers.js";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      cases: [],
    };
  }

  componentDidMount() {
    this.data();
  }
  data() {
    fetch(
      "https://opendata.arcgis.com/datasets/bbb2e4f589ba40d692fab712ae37b9ac_1.geojson"
    )
      .then((response) => response.json())
      .then(
        (responseJson) => this.setState({ cases: responseJson.features })
        // jsonData is parsed json object received from url
      );
    console.log(this.state.cases);
  }

  Map() {
    const [selectedCity, setSelectedCity] = useState(null);
    return (
      <GoogleMap defaultZoom={3} defaultCenter={{ lat: 0, lng: 0 }}>
        {this.state.cases.map((cases) => (
          <Marker
            key={cases.properties.OBJECTID}
            position={{
              lat: cases.properties.Lat,
              lng: cases.properties.Long_,
            }}
            onClick={() => {
              setSelectedCity(cases);
            }}
          />
        ))}
        {selectedCity && (
          <InfoWindow
            position={{
              lat: selectedCity.properties.Lat,
              lng: selectedCity.properties.Long_,
            }}
          >
            <div>city details</div>
          </InfoWindow>
        )}
      </GoogleMap>
    );
  }
  WrappedMap = withScriptjs(withGoogleMap(Map));
  render() {
    return (
      <div className="App" style={{ width: "100vw", height: "100vh" }}>
        <WrappedMap
          googleMapURL={
            "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDnwT8wbcgGl3mz0qhqb4dJpdHl6k-D--4"
          }
          loadingElement={<div style={{ height: "100%" }} />}
          containerElement={<div style={{ height: "100%" }} />}
          mapElement={<div style={{ height: "100%" }} />}
        />
      </div>
    );
  }
}

export default App;
