import React, { useState, useEffect } from "react";
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  InfoWindow,
  Marker,
} from "react-google-maps";
import Markers from "./components/Markers.js";
import Info from "./components/Info.js";
import { useFetch } from "./components/fetch.js";

function Map() {
  const [selectedCity, setSelectedCity] = useState(null);
  const apiURL =
    "https://opendata.arcgis.com/datasets/bbb2e4f589ba40d692fab712ae37b9ac_1.geojson";
  const { cases } = useFetch(apiURL);
  const [ccases, setCCases] = useState(null);
  useEffect(() => {
    setCCases(cases);
  }, [cases]);
  return (
    <GoogleMap defaultZoom={3} defaultCenter={{ lat: 0, lng: 0 }}>
      {ccases != null &&
        ccases.features.map((place) => (
          <Marker
            key={place.properties.OBJECTID}
            position={{
              lat: place.properties.Lat,
              lng: place.properties.Long_,
            }}
            onClick={setSelectedCity(place)}
          />
        ))}
      {selectedCity && (
        <InfoWindow
          position={{
            lat: selectedCity.properties.Lat,
            lng: selectedCity.properties.Long_,
          }}
        >
          <div>a</div>
        </InfoWindow>
      )}
    </GoogleMap>
  );
}

const WrappedMap = withScriptjs(withGoogleMap(Map));
function App() {
  return (
    <div className="App" style={{ width: "100vw", height: "100vh" }}>
      <WrappedMap
        googleMapURL={
          "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDnwT8wbcgGl3mz0qhqb4dJpdHl6k-D--4"
        }
        loadingElement={<div style={{ height: "100%" }} />}
        containerElement={<div style={{ height: "100%" }} />}
        mapElement={<div style={{ height: "100%" }} />}
      />
    </div>
  );
}
export default App;
