//Icon author: Icons made by <a href="https://www.flaticon.com/authors/freepik" title="Freepik">Freepik</a> from <a href="https://www.flaticon.com/" title="Flaticon"> www.flaticon.com</a>

import React, { useState, useEffect, useRef } from "react";
import {
  GoogleMap,
  withScriptjs,
  withGoogleMap,
  InfoWindow,
  Marker,
} from "react-google-maps";
import mapStyle from "./mapStyle.js";
import useSuperCluster from "use-supercluster";
import useSwr from "swr;";

function Map() {
  const [selectedCity, setSelectedCity] = useState({
    lat: null,
    lng: null,
    cty: null,
    cnt: null,
    cnf: null,
    rcv: null,
    dth: null,
  });

  function pass(lt, ln, ct, cn, cf, rv, dt) {
    if (!isNaN(lt) || lt != null) {
      if (!isNaN(ln) || ln != null) {
        setSelectedCity({
          lat: lt,
          lng: ln,
          cty: ct,
          cnt: cn,
          cnf: cf,
          rcv: rv,
          dth: dt,
        });
      }
    }
  }
  const [ccases, setCCases] = useState(null);
  //var obtain = false;
  //const [data, setData] = useState({ cases: null });
  useEffect(() => {
    fetch(
      "https://opendata.arcgis.com/datasets/bbb2e4f589ba40d692fab712ae37b9ac_1.geojson"
    )
      .then((response) => response.json())
      .then(
        (responseJson) => setCCases(responseJson)
        // jsonData is parsed json object received from url
      );
  }, [setCCases]);

  const mapRef = useRef();
  const [zoom, setZoom] = useState(4);
  const [bounds, setBounds] = useState(null);
  const points =
    ccases != null
      ? ccases.features.map((cases) => ({
          type: "Feature",
          properties: {
            cluster: false,
            objectId: cases.properties.OBJECTID,
            lat: cases.properties.Lat,
            lng: cases.properties.Long_,
            state: cases.properties.Province_State,
            country: cases.properties.Country_Region,
            confirmed: cases.properties.Confirmed,
            recovered: cases.properties.Recovered,
            deaths: cases.properties.Deaths,
          },
          geometry: {
            type: "Point",
            coordinates: [
              parseFloat(cases.properties.Long_),
              parseFloat(cases.properties.Lat),
            ],
          },
        }))
      : [];

  const { clusters } = useSuperCluster({
    points,
    bounds,
    zoom,
    options: { radius: 75, maxZoom: 20 },
  });
  return (
    <GoogleMap
      defaultZoom={4}
      defaultCenter={{ lat: 37.0902, lng: -95.7129 }}
      defaultOptions={{ styles: mapStyle }}
      yesIWantToUseGoogleMapApiInternals
      onGoogleApiLoaded={({ map }) => {
        mapRef.current = map;
      }}
      onChange={({ zoom, bounds }) => {
        setZoom(zoom);
        setBounds([bounds.nw.lng, bounds.se.lat, bounds.se.lng, bounds.nw.lat]);
      }}
    >
      {clusters.map((cluster) => {
        const [longitude, latitude] = cluster.geometry.coordinates;
        const {
          cluster: isCluster,
          point_count: pointCount,
        } = cluster.properties;

        if (isCluster) {
        }
        return (
          <Marker
            key={cluster.properties.objectId}
            position={{
              lat: latitude,
              lng: longitude,
            }}
            onClick={() =>
              pass(
                cluster.properties.lat,
                cluster.properties.lng,
                cluster.properties.state,
                cluster.properties.country,
                cluster.properties.confirmed,
                cluster.properties.recovered,
                cluster.properties.deaths
              )
            }
            icon={{
              url: "/coronavirus.svg",
              scaledSize: new window.google.maps.Size(
                Math.log(cluster.properties.confirmed) * 5,
                Math.log(cluster.properties.confirmed) * 5
              ),
            }}
          />
        );
      })}
    </GoogleMap>
  );
}

const WrappedMap = withScriptjs(withGoogleMap(Map));
function App() {
  return (
    <div className="App" style={{ width: "100vw", height: "100vh" }}>
      <WrappedMap
        googleMapURL={
          "https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyDnwT8wbcgGl3mz0qhqb4dJpdHl6k-D--4"
        }
        loadingElement={<div style={{ height: "100%" }} />}
        containerElement={<div style={{ height: "100%" }} />}
        mapElement={<div style={{ height: "100%" }} />}
      />
    </div>
  );
}
export default App;
